mapper.select:
WITH pagedData AS ( SELECT tcni.CMTY_NTT_SN AS cmtyNttSn
, REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(tcni.NTT_SJ, '\\b(?:https?://|www\\.)\\S+\\b', ' '), '&[a-zA-Z0-9#]+;', ' '), '<[^>]+>', ' '), '[\\n+\\r+]', ' '), '[\\x00-\\x1F]', ' '), '[^a-zA-Z가-힣0-9\\s+]', ' ') AS nttSj , REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(tcni.NTT_CN, '\\b(?:https?://|www\\.)\\S+\\b', ' '), '&[a-zA-Z0-9#]+;', ' '), '<[^>]+>', ' '), '[\\n+\\r+]', ' '), '[\\x00-\\x1F]', ' '), '[^a-zA-Z가-힣0-9\\s+]', ' ') AS nttCn
, CONCAT(DATE_FORMAT(tcni.NTT_REGIST_DT, '%Y-%m-%dT%H:%i:%s'), '+00:00') AS nttRegistDt , tcni.PRMBRSH_CNTNTS_AT AS prmbrshCntntsAt , tcni.CMTY_NTT_CTGRY_SN AS cmtyNttCtgrySn , tcni.NTT_OTHBC_SCOPE_CODE AS nttOthbcScopeCode , tcni.USER_SN AS userSn , tcni.NTT_NOTICE_AT AS nttNoticeAt , CONCAT(DATE_FORMAT(tcni.NTT_NOTICE_BEGIN_DT, '%Y-%m-%dT%H:%i:%s'), '+00:00') AS nttNoticeBeginDt , CONCAT(DATE_FORMAT(tcni.NTT_NOTICE_END_DT, '%Y-%m-%dT%H:%i:%s'), '+00:00') AS nttNoticeEndDt , CONCAT(DATE_FORMAT(tcni.SYS_REGIST_DT, '%Y-%m-%dT%H:%i:%s'), '+00:00') AS sysRegistDt , CONCAT(DATE_FORMAT(tcni.SYS_UPDT_DT, '%Y-%m-%dT%H:%i:%s'), '+00:00') AS sysUpdtDt , tcni.DELETE_AT AS deleteAt FROM PLAYCMMTYDB.TBCM_CMTY_NTT_INFO tcni JOIN PLAYCMMTYDB.TBAC_USER_INFO tui ON tcni.USER_SN = tui.USER_SN WHERE tcni.SANCTNS_AT = 'N' AND tcni.EXPSR_AT = 'Y' AND tui.USER_STTUS_CODE = 'S'
AND tcni.CMTY_NTT_SN IN
#{cmtyNttSn}
LIMIT #{offset}, #{pageSize}
) SELECT pagedData.* , tcnad.CMTY_NTT_ANSWER_SN AS tcnad_cmtyNttAnswerSn
, REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(REGEXP_REPLACE(tcnad.NTT_ANSWER_CN, '\\b(?:https?://|www\\.)\\S+\\b', ' '), '&[a-zA-Z0-9#]+;', ' '), '<[^>]+>', ' '), '[\\n+\\r+]', ' '), '[\\x00-\\x1F]', ' '), '[^a-zA-Z가-힣0-9\\s+]', ' ') AS tcnad_nttAnswerCn
, CONCAT(DATE_FORMAT(tcnad.NTT_ANSWER_REGIST_DT, '%Y-%m-%dT%H:%i:%s'), '+00:00') AS tcnad_nttAnswerRegistDt , CONCAT(DATE_FORMAT(tcnad.SYS_REGIST_DT, '%Y-%m-%dT%H:%i:%s'), '+00:00') AS tcnad_sysRegistDt , CONCAT(DATE_FORMAT(tcnad.SYS_UPDT_DT, '%Y-%m-%dT%H:%i:%s'), '+00:00') AS tcnad_sysUpdtDt , tcnad.DELETE_AT AS tcnad_deleteAt , tucnbi.USER_NTT_BLCKG_SN AS tucnbi_userNttBlckgSn , tucnbi.USER_SN AS tucnbi_userSn , CONCAT(DATE_FORMAT(tucnbi.SYS_REGIST_DT, '%Y-%m-%dT%H:%i:%s'), '+00:00') AS tucnbi_sysRegistDt , CONCAT(DATE_FORMAT(tucnbi.SYS_UPDT_DT, '%Y-%m-%dT%H:%i:%s'), '+00:00') AS tucnbi_sysUpdtDt , tucnbi.DELETE_AT AS tucnbi_deleteAt , tucrbi.USER_FREND_BLCKG_SN AS tucrbi_userFrendBlckgSn , tucrbi.USER_SN AS tucrbi_userSn , tucrbi.BLCKG_USER_SN AS tucrbi_blckgUserSn , CONCAT(DATE_FORMAT(tucrbi.SYS_REGIST_DT, '%Y-%m-%dT%H:%i:%s'), '+00:00') AS tucrbi_sysRegistDt , CONCAT(DATE_FORMAT(tucrbi.SYS_UPDT_DT, '%Y-%m-%dT%H:%i:%s'), '+00:00') AS tucrbi_sysUpdtDt , tucrbi.DELETE_AT AS tucrbi_deleteAt , tucrbi2.USER_FREND_BLCKG_SN AS tucrbi2_userFrendBlckgSn , tucrbi2.USER_SN AS tucrbi2_userSn , tucrbi2.BLCKG_USER_SN AS tucrbi2_blckgUserSn , CONCAT(DATE_FORMAT(tucrbi2.SYS_REGIST_DT, '%Y-%m-%dT%H:%i:%s'), '+00:00') AS tucrbi2_sysRegistDt , CONCAT(DATE_FORMAT(tucrbi2.SYS_UPDT_DT, '%Y-%m-%dT%H:%i:%s'), '+00:00') AS tucrbi2_sysUpdtDt , tucrbi2.DELETE_AT AS tucrbi2_deleteAt FROM pagedData LEFT JOIN PLAYCMMTYDB.TBCM_CMTY_NTT_ANSWER_DETAIL tcnad ON pagedData.cmtyNttSn = tcnad.CMTY_NTT_SN LEFT JOIN PLAYCMMTYDB.TBAC_USER_CMTY_NTT_BLCKG_INFO tucnbi on pagedData.cmtyNttSn = tucnbi.CMTY_NTT_SN AND tucnbi.NTT_TY_CODE = 'N' LEFT JOIN PLAYCMMTYDB.TBAC_USER_CMTY_FREND_BLCKG_INFO tucrbi ON pagedData.userSn = tucrbi.BLCKG_USER_SN AND tucrbi.BLCKG_AT = 'Y' LEFT JOIN PLAYCMMTYDB.TBAC_USER_CMTY_FREND_BLCKG_INFO tucrbi2 ON pagedData.userSn = tucrbi2.USER_SN AND tucrbi2.BLCKG_AT = 'Y'

SELECT * FROM ( SELECT tcni.CMTY_NTT_SN FROM PLAYCMMTYDB.TBCM_CMTY_NTT_INFO tcni JOIN PLAYCMMTYDB.TBAC_USER_INFO tui ON tcni.USER_SN = tui.USER_SN WHERE tcni.SANCTNS_AT = 'N' AND tcni.EXPSR_AT = 'Y' AND tui.USER_STTUS_CODE = 'S'
AND ((tcni.SYS_UPDT_DT IS NULL AND tcni.SYS_REGIST_DT > #{cmtyNttMaxSysRegistDt}) OR tcni.SYS_UPDT_DT > #{cmtyNttMaxSysUpdtDt})
AND tcni.SYS_UPDT_DT IS NULL AND tcni.SYS_REGIST_DT > #{cmtyNttMaxSysRegistDt}
AND tcni.SYS_UPDT_DT > #{cmtyNttMaxSysUpdtDt}
UNION
SELECT tcni.CMTY_NTT_SN FROM PLAYCMMTYDB.TBCM_CMTY_NTT_INFO tcni JOIN PLAYCMMTYDB.TBAC_USER_INFO tui ON tcni.USER_SN = tui.USER_SN JOIN PLAYCMMTYDB.TBCM_CMTY_NTT_ANSWER_DETAIL tcnad ON tcni.CMTY_NTT_SN = tcnad.CMTY_NTT_SN WHERE tcni.SANCTNS_AT = 'N' AND tcni.EXPSR_AT = 'Y' AND tui.USER_STTUS_CODE = 'S'
AND ((tcnad.SYS_UPDT_DT IS NULL AND tcnad.SYS_REGIST_DT > #{cmtyNttAnswersMaxSysRegistDt}) OR tcnad.SYS_UPDT_DT > #{cmtyNttAnswersMaxSysUpdtDt})
AND tcnad.SYS_UPDT_DT IS NULL AND tcnad.SYS_REGIST_DT > #{cmtyNttAnswersMaxSysRegistDt}
AND tcnad.SYS_UPDT_DT > #{cmtyNttAnswersMaxSysUpdtDt}
UNION
SELECT tcni.CMTY_NTT_SN FROM PLAYCMMTYDB.TBCM_CMTY_NTT_INFO tcni JOIN PLAYCMMTYDB.TBAC_USER_INFO tui ON tcni.USER_SN = tui.USER_SN JOIN PLAYCMMTYDB.TBAC_USER_CMTY_NTT_BLCKG_INFO tucnbi ON tcni.CMTY_NTT_SN = tucnbi.CMTY_NTT_SN WHERE tcni.SANCTNS_AT = 'N' AND tcni.EXPSR_AT = 'Y' AND tui.USER_STTUS_CODE = 'S' AND tucnbi.NTT_TY_CODE = 'N'
AND ((tucnbi.SYS_UPDT_DT IS NULL AND tucnbi.SYS_REGIST_DT > #{nttBlckgInfoMaxSysRegistDt}) OR tucnbi.SYS_UPDT_DT > #{nttBlckgInfoMaxSysUpdtDt})
AND tucnbi.SYS_UPDT_DT IS NULL AND tucnbi.SYS_REGIST_DT > #{nttBlckgInfoMaxSysRegistDt}
AND tucnbi.SYS_UPDT_DT > #{nttBlckgInfoMaxSysUpdtDt}
UNION
SELECT tcni.CMTY_NTT_SN FROM PLAYCMMTYDB.TBCM_CMTY_NTT_INFO tcni JOIN PLAYCMMTYDB.TBAC_USER_INFO tui ON tcni.USER_SN = tui.USER_SN JOIN PLAYCMMTYDB.TBAC_USER_CMTY_FREND_BLCKG_INFO tucrbi ON tcni.USER_SN = tucrbi.BLCKG_USER_SN AND tucrbi.BLCKG_AT = 'Y' WHERE tcni.SANCTNS_AT = 'N' AND tcni.EXPSR_AT = 'Y' AND tui.USER_STTUS_CODE = 'S'
AND ((tucrbi.SYS_UPDT_DT IS NULL AND tucrbi.SYS_REGIST_DT > #{cmtyFrendBlckgInfoMaxSysRegistDt}) OR tucrbi.SYS_UPDT_DT > #{cmtyFrendBlckgInfoMaxSysUpdtDt})
AND tucrbi.SYS_UPDT_DT IS NULL AND tucrbi.SYS_REGIST_DT > #{cmtyFrendBlckgInfoMaxSysRegistDt}
AND tucrbi.SYS_UPDT_DT > #{cmtyFrendBlckgInfoMaxSysUpdtDt}
UNION
SELECT tcni.CMTY_NTT_SN FROM PLAYCMMTYDB.TBCM_CMTY_NTT_INFO tcni JOIN PLAYCMMTYDB.TBAC_USER_INFO tui ON tcni.USER_SN = tui.USER_SN JOIN PLAYCMMTYDB.TBAC_USER_CMTY_FREND_BLCKG_INFO tucrbi ON tcni.USER_SN = tucrbi.USER_SN AND tucrbi.BLCKG_AT = 'Y' WHERE tcni.SANCTNS_AT = 'N' AND tcni.EXPSR_AT = 'Y' AND tui.USER_STTUS_CODE = 'S'
AND ((tucrbi.SYS_UPDT_DT IS NULL AND tucrbi.SYS_REGIST_DT > #{cmtyFrendBlckgInfo2MaxSysRegistDt}) OR tucrbi.SYS_UPDT_DT > #{cmtyFrendBlckgInfo2MaxSysUpdtDt})
AND tucrbi.SYS_UPDT_DT IS NULL AND tucrbi.SYS_REGIST_DT > #{cmtyFrendBlckgInfo2MaxSysRegistDt}
AND tucrbi.SYS_UPDT_DT > #{cmtyFrendBlckgInfo2MaxSysUpdtDt}
) T
LIMIT #{offset}, #{pageSize}
